#!/bin/bash
process_sonar_data() {
  local sonar_file=$1
  echo "Processing sonar data from $sonar_file"
  local fish_count=$(grep -c "fish" "$sonar_file" 2>/dev/null || echo 0)
  echo "Detected $fish_count fish schools"
  return $fish_count
}

process_sonar_data "sonar.log"

#!/bin/bash
generate_report() {
  echo "=== DAILY OCEAN REPORT ==="
  echo "Date: $(date '+%Y-%m-%d %H:%M')"
  echo "Weather: $([[ $(date +%u) -le 5 ]] && echo 'Calm seas' || echo 'Stormy weather')"
  echo "Visibility: $((RANDOM % 50 + 10))m"
  echo "Report generated successfully!"
}

generate_report > "ocean_report_$(date +%Y%m%d).txt"
