.castle {
    color: gray;
    background-color: lightgray;
    font-family: 'Medieval', serif;
    font-size: 16px;
    text-align: center;
}

#throne-room {
    width: 300px;
    height: 200px;
    border: 3px solid gold;
    margin: 20px auto;
    padding: 15px;
}

.knight {
    font-weight: bold;
    text-decoration: underline;
    text-transform: uppercase;
    letter-spacing: 2px;
    line-height: 1.5;
}

.castle-wall {
    background: linear-gradient(to bottom, #8B4513, #A0522D);
    border-radius: 10px;
    box-shadow: 2px 2px 8px rgba(0, 0, 0, 0.5);
}

.banner {
    display: block;
    position: relative;
    top: 10px;
    left: 20px;
    z-index: 100;
}

.great-hall {
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 20px;
    flex-wrap: wrap;
}

.castle-grid {
    display: grid;
    grid-template-columns: 1fr 2fr 1fr;
    grid-template-rows: auto 1fr auto;
    grid-gap: 10px;
    height: 100vh;
}

.drawbridge:hover {
    transform: rotate(-45deg);
    transition: transform 0.5s ease;
}
.moat {
    background: blue;
    opacity: 0.7;
}

@media (max-width: 768px) {
    .castle {
        width: 100%;
        padding: 10px;
    }
    .tower {
        height: 150px;
    }
}

.knight::before {
    content: '‚öîÔ∏è';
    margin-right: 5px;
}
.knight::after {
    content: ' - Defender of the Realm';
    font-style: italic;
}

.armor {
    background: url('chainmail.png') repeat;
    background-size: 50px 50px;
    border: 2px solid silver;
    background-attachment: fixed;
}

.castle-entrance {
    clip-path: polygon(50% 0%, 0% 100%, 100% 100%);
    background: #654321;
    width: 200px;
    height: 150px;
}

@keyframes flag-wave {
    0% { transform: rotate(0deg); }
    25% { transform: rotate(5deg); }
    75% { transform: rotate(-5deg); }
    100% { transform: rotate(0deg); }
}
.banner {
    animation: flag-wave 2s infinite;
}

.torch {
    position: relative;
}
.torch::before {
    content: '';
    position: absolute;
    top: -10px;
    left: 50%;
    width: 20px;
    height: 20px;
    background: radial-gradient(circle, orange, red);
    border-radius: 50%;
    animation: flicker 1s infinite alternate;
}

.round-table {
    display: grid;
    place-items: center;
    grid-template-columns: repeat(auto-fit, minmax(100px, 1fr));
    gap: 20px;
    border-radius: 50%;
    aspect-ratio: 1;
}

.castle-shadow {
    filter: drop-shadow(10px 10px 20px rgba(0, 0, 0, 0.6));
}
.stained-glass {
    filter: hue-rotate(45deg) saturate(1.5) brightness(1.2);
}

.quest-scroll {
    writing-mode: vertical-rl;
    text-orientation: mixed;
    border: 3px solid #8B4513;
    background: parchment;
    padding: 20px;
    scroll-behavior: smooth;
}

.heraldic-shield {
    width: 100px;
    height: 120px;
    background: linear-gradient(45deg, red 50%, gold 50%);
    clip-path: polygon(50% 0%, 0% 40%, 0% 90%, 50% 100%, 100% 90%, 100% 40%);
    border: 3px solid #333;
}

.tournament-arena {
    display: subgrid;
    container-type: inline-size;
}
@container (min-width: 400px) {
    .knight-tile {
        grid-column: span 2;
        font-size: 1.2em;
    }
}

.medieval-layout {
    display: grid;
    grid-template-areas:
        "header header header"
        "sidebar main tower"
        "footer footer footer";
    grid-template-columns: 200px 1fr 150px;
    grid-template-rows: auto 1fr auto;
    min-height: 100vh;
}
