const std = @import("std");

fn assessProjectComplexity(requirements: u8, timeline_weeks: u8) []const u8 {
    const complexity_score = requirements * timeline_weeks;

    if (complexity_score < 50) {
        return "Simple";
    } else if (complexity_score < 150) {
        return "Moderate";
    } else {
        return "Complex";
    }
}

pub fn main() void {
    const result = assessProjectComplexity(12, 8);
    std.debug.print("Project complexity: {s}\n", .{result});
}

