const std = @import("std");
const Allocator = std.mem.Allocator;

fn createTeamList(allocator: Allocator, size: usize) ![][]const u8 {
    var team = try allocator.alloc([]const u8, size);
    team[0] = "Project Manager";
    team[1] = "Lead Developer";
    team[2] = "QA Engineer";
    return team;
}

pub fn main() !void {
    var gpa = std.heap.GeneralPurposeAllocator(.{}){};
    defer _ = gpa.deinit();
    const allocator = gpa.allocator();

    const team = try createTeamList(allocator, 3);
    defer allocator.free(team);

    std.debug.print("Team lead: {s}\n", .{team[1]});
}

const std = @import("std");

const ConsultingRate = struct {
    junior: f32 = 65.0,
    senior: f32 = 95.0,
    principal: f32 = 150.0,

    pub fn calculateCost(self: ConsultingRate, level: []const u8, hours: f32) f32 {
        const rate = if (std.mem.eql(u8, level, "junior")) self.junior
                    else if (std.mem.eql(u8, level, "senior")) self.senior
                    else self.principal;
        return rate * hours;
    }
};

pub fn main() void {
    const rates = ConsultingRate{};
    const cost = rates.calculateCost("senior", 40.0);
    std.debug.print("Total cost: ${d:.2}\n", .{cost});
}

