const std = @import("std");
const testing = std.testing;

fn calculateProjectDuration(tasks: u8, team_size: u8) u8 {
    return (tasks * 2) / team_size;
}

test "project duration calculation" {
    const duration = calculateProjectDuration(20, 4);
    try testing.expect(duration == 10);

    const small_project = calculateProjectDuration(8, 2);
    try testing.expect(small_project == 8);
}

const std = @import("std");
const Thread = std.Thread;

fn processClientRequests(thread_id: u32) void {
    std.debug.print("Thread {d}: Processing client requests\n", .{thread_id});
    std.time.sleep(1000000000); // 1 second
    std.debug.print("Thread {d}: Requests completed\n", .{thread_id});
}

pub fn main() !void {
    var threads: [3]Thread = undefined;

    for (&threads, 0..) |*thread, i| {
        thread.* = try Thread.spawn(.{}, processClientRequests, .{@intCast(u32, i)});
    }

    for (threads) |thread| {
        thread.join();
    }

    std.debug.print("All client requests processed\n");
}

const std = @import("std");

const ConfigError = error{ InvalidFormat, MissingField };

const ServerConfig = struct {
    host: []const u8,
    port: u16,
    ssl_enabled: bool,

    pub fn fromJson(json_str: []const u8) ConfigError!ServerConfig {
