const std = @import("std");
const math = std.math;

inline fn calculateSLA(uptime_hours: f64, total_hours: f64) f64 {
    return (uptime_hours / total_hours) * 100.0;
}

fn generatePerformanceReport() void {
    const systems = [_]struct { name: []const u8, uptime: f64, total: f64 }{
        .{ .name = "Web Server", .uptime = 8736.0, .total = 8760.0 },
        .{ .name = "Database", .uptime = 8752.0, .total = 8760.0 },
        .{ .name = "Cache Layer", .uptime = 8720.0, .total = 8760.0 },
    };

    for (systems) |system| {
        const sla = calculateSLA(system.uptime, system.total);
        std.debug.print("{s}: {d:.3}% uptime\n", .{system.name, sla});
    }
}

pub fn main() void {
    std.debug.print("=== System Performance Report ===\n");
    generatePerformanceReport();
}

const std = @import("std");
const Allocator = std.mem.Allocator;
const HashMap = std.HashMap;

const ClientMetrics = struct {
    requests: u32,
    response_time: f32,
    success_rate: f32,
};

pub fn main() !void {
    var gpa = std.heap.GeneralPurposeAllocator(.{}){};
    defer _ = gpa.deinit();
    const allocator = gpa.allocator();

    var client_stats = HashMap(u32, ClientMetrics, std.hash_map.default_hash, std.hash_map.default_eql, std.hash_map.default_max_load_percentage).init(allocator);
    defer client_stats.deinit();

    try client_stats.put(1001, ClientMetrics{ .requests = 1250, .response_time = 0.045, .success_rate = 99.8 });
    try client_stats.put(1002, ClientMetrics{ .requests = 890, .response_time = 0.032, .success_rate = 100.0 });

    const client_1001_stats = client_stats.get(1001).?;
    std.debug.print("Client 1001: {d} requests, {d:.1}% success\n", .{client_1001_stats.requests, client_1001_stats.success_rate});
}

